import { reqRegisterCode } from "@/api"

const state = {
    code:''
}
const mutations = {
    GETREGISTERCODE(state,data){
        state.code = data
    }
}
const actions = {
    async getRegisterCode({commit},phone){
        let result = await reqRegisterCode(phone)
        if(result.code===200){
            commit("GETREGISTERCODE",result.data)
        }else{

        }
    }
}
const getters = {}
export default {
    state,
    mutations,
    actions,
    getters
}
