import "core-js/modules/es.array.push.js";
import QRCode from 'qrcode';
export default {
  name: 'Pay',

  data() {
    return {
      payInfo: {},
      timer: null,
      payResult: ''
    };
  },

  computed: {
    orderId() {
      return this.$route.query.orderId;
    }

  },

  mounted() {
    this.getPayInfo();
  },

  methods: {
    async getPayInfo() {
      let result = await this.$API.reqPayInfo(this.orderId);

      if (result.code == 200) {
        this.payInfo = result.data;
      } else {
        result.data;
      }
    },

    async open() {
      let url = await QRCode.toDataURL('weixin://wxpay/bizpayurl?pr=V0pg6Bczz');
      this.$alert(`<img src=${url} />`, '请你微信支付', {
        center: true,
        showCancelButton: true,
        cancelButtonText: '支付遇到问题',
        confirmButtonText: '支付成功',
        showClose: false,
        dangerouslyUseHTMLString: true,
        beforeClose: (type, instance, done) => {
          if (type == 'confirm') {
            if (this.payResult == 200) {
              clearInterval(this.timer);
              this.timer = null;
              this.$router.push('/paySuccess');
              done();
            } else {
              alert("支付还未成功！");
            }
          } else if (type == 'cancel') {
            alert('请联系管理员');
            clearInterval(this.timer);
            this.timer = null;
          }
        }
      });

      if (!this.timer) {
        this.timer = setInterval(async () => {
          let result = await this.$API.reqPayInfo(this.orderId);

          if (result.code == 200) {
            clearInterval(this.timer);
            this.timer = null;
            this.payResult = result.code;
            this.$msgbox.close();
            this.$router.push('/paySuccess');
          }
        }, 1000);
      }
    }

  }
};
