import {
    reqGoodsInfo
} from "@/api"
const state = {
    goodsInfo: {}
}
const actions = {
    async getGoodsInfo({
        commit
    }, skuid) {
        let result = await reqGoodsInfo(skuid)
        if (result.code === 200) {
            commit('GETGOODSINFO', result)
        }
    }
}
const mutations = {
    GETGOODSINFO(state, goodsInfo) {
        state.goodsInfo = goodsInfo
    }
}
const getters = {
    categoryView(state){
        return state.goodsInfo.data.categoryView || []
    }
}
export default {
    state,
    actions,
    mutations,
    getters
}
