import "core-js/modules/es.array.push.js";
import { mapState } from 'vuex';
export default {
  name: 'TypeNav',

  mounted() {
    if (this.$route.path === '/home') this.isShow = true;else this.isShow = false;
  },

  computed: { ...mapState({
      categoryList: state => {
        return state.home.categoryList.slice(0, 16);
      }
    })
  },
  methods: {
    goSearch(event) {
      let {
        categoryname,
        category1id,
        category2id,
        category3id
      } = event.target.dataset;

      if (categoryname) {
        let location = {
          name: 'search'
        };
        let query = {
          categoryName: categoryname
        };

        if (category1id) {
          query.category1Id = category1id;
        } else if (category2id) {
          query.category2Id = category2id;
        } else if (category3id) {
          query.category3Id = category3id;
        }

        if (this.$route.params) {
          location.params = this.$route.params;
        }

        location.query = query;
        this.$router.push(location);
      }
    },

    enterShow() {
      this.isShow = true;
    },

    leaveShow() {
      if (this.$route.path === '/home') this.isShow = true;else this.isShow = false;
    }

  },

  data() {
    return {
      isShow: true
    };
  }

};
