import {
    reqCartList,
    reqDeletCartById,
    reqUpdateCheckedById
} from "@/api"

const state = {
    cartList: []
}
const actions = {
    async getCartList({
        commit
    }) {
        let result = await reqCartList()
        if (result.code == 200) {
            commit("GETCARTLIST", result.data)
        }
    },
    async deleteCartListBySkuId({
        commit
    }, skuId) {
        let result = await reqDeletCartById(skuId)
        if (result.code == 200) {
            return 'ok'
        } else {
            Promise.reject(new Error('faile'))
        }
    },
    async updateCheckBySkuId({
        commit
    }, {
        skuId,
        isChecked
    }) {
        let result = await reqUpdateCheckedById(skuId, isChecked)
        if (result.code === 200) {
            return 'ok'
        } else {
            return Promise.reject(new Error('faile'))
        }
    },
    deletAllCheckedCart({
        dispatch,
        getters
    }) {
        getters.cartInfo.cartInfoList.forEach(item => {
            item.isChecked === 1 ? dispatch('deleteCartListBySkuId', item.skuId) : ''
        });
    }
}
const mutations = {
    GETCARTLIST(state, cartList) {
        state.cartList = cartList
    }
}
const getters = {
    cartInfo(state) {
        return state.cartList[0] || {}
    },
}
export default {
    state,
    actions,
    mutations,
    getters
}
