import {
    reqCartList
} from "@/api"

const state = {
    cartList: []
}
const actions = {
    async getCartList({
        commit
    }) {
        let result = await reqCartList()
        if (result.code == 200) {
            commit("GETCARTLIST", result.data)
        }
    }
}
const mutations = {
    GETCARTLIST(state, cartList) {
        state.cartList = cartList
    }
}
const getters = {
    cartInfoList(state) {
        setTimeout(()=>{return state.cartList[0].cartInfoList || []},1000)
        
    },

}
export default {
    state,
    actions,
    mutations,
    getters
}
