import {
    reqGoodsInfo
} from "@/api"
const state = {
    goodsInfo: {}
}
const actions = {
    async getGoodsInfo({
        commit
    }, skuId) {
        let result = await reqGoodsInfo(skuId)
        if (result.code === 200) {
            commit('GETGOODSINFO', result)
        }
    }
}
const mutations = {
    GETGOODSINFO(state, goodsInfo) {
        state.goodsInfo = goodsInfo
    }
}
const getters = {}
export default {
    state,
    actions,
    mutations,
    getters
}
