import {
    reqCartList, reqDeletCartById
} from "@/api"

const state = {
    cartList: []
}
const actions = {
    async getCartList({
        commit
    }) {
        let result = await reqCartList()
        if (result.code == 200) {
            commit("GETCARTLIST", result.data)
        }
    },
    async deleteCartListBySkuId({commit},skuId){
        let result = await reqDeletCartById()
        if(result.code == 200){
            return 'ok'
        }else{
            Promise.reject(new Error('faile'))
        }
    }
}
const mutations = {
    GETCARTLIST(state, cartList) {
        state.cartList = cartList
    }
}
const getters = {
    cartInfo(state) {
        return state.cartList[0] || {} 
    },
}
export default {
    state,
    actions,
    mutations,
    getters
}
