import {
    reqGoodsInfo
} from "@/api"
const state = {
    goodInfo: {}
}
const actions = {
    async getGoodsInfo({
        commit
    }, skuid) {
        let result = await reqGoodsInfo(skuid)
        if (result.code === 200) {
            commit('GETGOODSINFO', result)
        }
    }
}
const mutations = {
    GETGOODSINFO(state, goodInfo) {
        state.goodInfo = goodInfo
    }
}
const getters = {
    categoryView(){
        return state.goodInfo.data.categoryView || {}
    },
    skuInfo(){
        return state.goodInfo.skuInfo || {}
    },
    getCode(){
        return state.goodInfo.code
    }
}
export default {
    state,
    actions,
    mutations,
    getters
}
