import "core-js/modules/es.array.push.js";
export default {
  name: "Register",

  data() {
    return {
      phone: "",
      code: "",
      password: '',
      repassword: '',
      isAgree: false
    };
  },

  methods: {
    getCode() {
      this.phone && this.$store.dispatch("getRegisterCode", this.phone).then(() => {
        this.code = this.$store.state.user.code;
      }).catch(error => {
        console.log(error.message);
      });
    },

    userRegister() {
      const success = this.$validator.validateAll();
      const data = {
        phone: this.phone,
        code: this.code,
        password: this.password
      };

      if (success) {
        this.$store.dispatch('userRegister', data).then(() => {
          this.$router.push('/login');
        }).catch(error => {
          console.log(error.message);
        });
      }
    },

    changeAgree(event) {
      this.isAgree = event.target.checked;
    }

  }
};
