import {
    reqRegisterCode, reqUserRegister
} from "@/api"

const state = {
    code: ''
}
const mutations = {
    GETREGISTERCODE(state, data) {
        state.code = data
    }
}
const actions = {
    async getRegisterCode({
        commit
    }, phone) {
        let result = await reqRegisterCode(phone)
        if (result.code === 200) {
            commit("GETREGISTERCODE", result.data)
        }
    },
    async userRegister({commit},data){
        let result = await reqUserRegister(data)
        if(result.code === 200){
            // commit('')
        }
    }
}
const getters = {}
export default {
    state,
    mutations,
    actions,
    getters
}
