import "core-js/modules/es.array.push.js";
import { mapGetters } from 'vuex';
import ImageList from './ImageList/ImageList';
import Zoom from './Zoom/Zoom';
export default {
  name: 'Detail',

  data() {
    return {
      skuNum: 1
    };
  },

  components: {
    ImageList,
    Zoom
  },

  mounted() {
    this.$store.dispatch('getGoodsInfo', this.$route.params.skuid);
  },

  computed: { ...mapGetters(['categoryView', 'skuInfo', 'spuSaleAttrList']),

    skuImageList() {
      return this.skuInfo.skuImageList || [];
    }

  },
  methods: {
    changeActive(sonValue, fatherValue) {
      fatherValue.forEach(item => {
        item.isChecked = '0';
      });
      sonValue.isChecked = '1';
    },

    plus() {
      this.skuNum = this.skuNum + 1;
    },

    mins() {
      if (this.skuNum <= 1) this.skuNum = 1;else this.skuNum = this.skuNum - 1;
    },

    handler(event) {
      let value = event.target.value;

      if (isNaN(value) || value < 1) {
        this.skuNum = 1;
      } else {
        this.skuNum = parseInt(value);
      }
    },

    async addShopCar() {
      try {
        await this.$store.dispatch('addOrUpdateShopCar', {
          skuid: this.$route.params.skuid,
          skuNum: this.skuNum
        });
        sessionStorage.setItem('SKUINFO', JSON.stringify(this.skuInfo));
        this.$router.push({
          name: 'addCartSuccess',
          query: {
            skuNum: this.skuNum
          }
        });
      } catch (error) {
        alert(error.message);
      }
    }

  }
};
